import React, { useState } from 'react'

export default function TextForm(props) {
    const [text, settext] = useState("");
    const handleUpClick = () => {
        // console.log("Uppercase was clicked"+text);
        let newText = text.toUpperCase();
        settext(newText);
    }
    const handleLowClick = () => {
        let newText = text.toLowerCase();
        settext(newText);
    }
    const clearTextArea = () => {
        if(window.confirm("Do you really want to clear the text?")){
            settext("")
        }else{
            let newText = text;
            settext(newText);
        }

    }
    const textCopy = () => {
        let text=document.getElementById("mybox");
        text.select();
        navigator.clipboard.writeText(text.value);
    }
    const italic = () => {
        let newText = text;

    }
    const handleOnChange = (event) => {
        // console.log("On change");
        settext(event.target.value)
    }
    return (
        <>
            <div className="container">
                <h4>{props.heading}</h4>
                <div className="mb-3">
                    <textarea className="form-control" value={text} onChange={handleOnChange} id="mybox" rows="8"></textarea>
                </div>
                <button className="btn btn-primary" onClick={handleUpClick}>Uppercase</button>
                <button className="btn btn-primary mx-2" onClick={handleLowClick}>LowerCase</button>
                <button className="btn btn-primary mx-2" onClick={textCopy}>Copy Text</button>
                <button className="btn btn-primary mx-2" onClick={italic}>Italic</button>
                <button className="btn btn-primary mx-2 btn-danger" onClick={clearTextArea}>Clear Text </button>
            </div>
            <div className="container my-3">
                <h2>Your text summary</h2>
                <p>{text.split(' ').length} words and {text.length} characters</p>
                <p>{0.008 * text.split(" ").length} minutes need to read</p>
                <h3>Preview</h3>
                <p>{text}</p>
            </div>
        </>
    )
}
