import React, { useState } from 'react'

export default function TextForm(props) {
    const [text, settext] = useState("Enter text here");
    const handleUpClick = () => {
        // console.log("Uppercase was clicked"+text);
        let newText = text.toUpperCase();
        settext(newText);
    }
    const handleLowClick = () => {
        // console.log("Uppercase was clicked"+text);
        let newText = text.toLowerCase();
        settext(newText);
    }
    const clearTextArea = () => {
        // console.log("Uppercase was clicked"+text);
        alert("Do you really want to clear the re")
    }
    const handleOnChange = (event) => {
        // console.log("On change");
        settext(event.target.value)
    }
    return (
        <>
            <div className="container">
                <h4>{props.heading}</h4>
                <div className="mb-3">
                    <textarea className="form-control" value={text} onChange={handleOnChange} id="mybox" rows="8"></textarea>
                </div>
                <button className="btn btn-primary" onClick={handleUpClick}>Convert to upper case</button>
                <button className="btn btn-primary mx-2" onClick={handleLowClick}>Convert to lowercase case</button>
                <button className="btn btn-primary mx-2" onClick={clearTextArea}>Clear Text Area</button>
            </div>
            <div className="container my-3">
                <h2>Your text summary</h2>
                <p>{text.split(' ').length} words and {text.length} characters</p>
                <p>{0.008 * text.split(" ").length} minutes need to read</p>
                <h3>Preview</h3>
                <p>{text}</p>
            </div>
        </>
    )
}
